var CANDIDATE = CANDIDATE || {};
(function (n) {
	"use strict";
	CANDIDATE = {
		init: function () {
			this.tab_candidate();
			this.social_candidate();
			this.login_notice();
		},
		tab_candidate: function () {
			function i(i) {
				n(".jobs-candidate-sidebar ul li").removeClass("active");
				n(i).addClass("active");
				var a = n(i).find("a").attr("href");
				n(".tab-info-candidate").hide();
				n(a).show();
			}
			n(".tab-candidate li").click(function () {
				i(this);
				return false;
			});
			i(n(".jobs-candidate-sidebar ul li:first-child"));
		},
		social_candidate: function () {
			n("body").on(
				"click",
				"#candidate-submit-social .soical-remove-inner",
				function () {
					var i = n(this).closest(".clone-wrap");
					n(i).find(".field-wrap").slideToggle();
				}
			);
		},
		login_notice: function () {
			var i = n(".btn-login.notice-employer").data("notice");
			if (n(".btn-login").hasClass("notice-employer")) {
				n("#popup-form .notice").html(
					'<i class="fal fa-exclamation-circle"></i>' + i
				);
			}
		},
	};
	n(document).ready(function () {
		CANDIDATE.init();
	});
})(jQuery);
